// Get configuration from Workflow Configuration node
const config = $('Workflow Configuration for Hourly').first().json;
const dateColumnName = config.dateColumnName;
const timeColumnName = config.timeColumnName;

// Get current time and calculate target time (1 hour from now)
const now = new Date();
const targetTime = new Date(now.getTime() + 60 * 60 * 1000); // 1 hour from now

// Filter appointments that are exactly 1 hour away
const filteredItems = $input.all().filter(item => {
  const appointmentDate = item.json[dateColumnName];
  const appointmentTime = item.json[timeColumnName];

  if (!appointmentDate || !appointmentTime) {
    return false;
  }

  // Combine date and time into a single Date object
  const appointmentDateTime = new Date(`${appointmentDate} ${appointmentTime}`);

  // Check if appointment is within the next hour (between 55-65 minutes to account for timing)
  const timeDiff = appointmentDateTime.getTime() - now.getTime();
  const minutesAway = timeDiff / (1000 * 60);

  // Return true if appointment is between 55 and 65 minutes away
  return minutesAway >= 55 && minutesAway <= 65;
});

return filteredItems;

