function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0;
    var v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const filterStyles = {
  'cartoon': 'vibrant cartoon style with bold outlines, bright colors, and smooth cel-shading like a Disney or Pixar animation',
  'oil_painting': 'classic oil painting with visible brushstrokes, rich textures, and deep colors in the style of Renaissance masters',
  'anime': 'Japanese anime style with large expressive eyes, clean lines, and vibrant colors',
  'vintage': 'vintage 1970s photograph with warm sepia tones, soft film grain, light leaks, and slight vignetting',
  'neon': 'cyberpunk neon style with glowing neon edges, dark background, and vibrant pink/blue/purple color scheme',
  'watercolor': 'soft watercolor painting with flowing colors, gentle washes, and delicate brush effects',
  'sketch': 'detailed pencil sketch with professional shading, hatching, and fine line work',
  'pop_art': 'Andy Warhol pop art style with bold primary colors, halftone dots, and high contrast'
};

const filterName = $json.body.filter.toLowerCase();
const styleDescription = filterStyles[filterName];

if (!styleDescription) {
  throw new Error('Invalid filter: ' + filterName);
}

let imageData = $json.body.image;
if (!imageData.startsWith('data:')) {
  imageData = 'data:image/jpeg;base64,' + imageData;
}

const uuid = generateUUID();
const filename = uuid + '.png';

return {
  json: {
    filter: filterName,
    styleDescription: styleDescription,
    imageDataUrl: imageData,
    filename: filename,
    uuid: uuid,
    timestamp: new Date().toISOString(),
    session_id: $json.body.session_id || null
  }
};

