const visionResponse = $json;
const prevData = $('Code - Map Filter to Style').item.json;

// Extract the image description from GPT-4o Vision
let imageDescription = '';
if (visionResponse.choices && visionResponse.choices[0] && visionResponse.choices[0].message) {
  imageDescription = visionResponse.choices[0].message.content;
} else {
  throw new Error('Failed to get image description from GPT-4o Vision');
}

// Create the DALL-E prompt combining the description with the style
const dallePrompt = `Create an image in ${prevData.styleDescription}. The image should depict: ${imageDescription}`;

return {
  json: {
    dallePrompt: dallePrompt,
    imageDescription: imageDescription,
    filter: prevData.filter,
    styleDescription: prevData.styleDescription,
    filename: prevData.filename,
    uuid: prevData.uuid,
    timestamp: prevData.timestamp,
    session_id: prevData.session_id
  }
};

const openaiResponse = $json;
let generatedImageB64;

if (openaiResponse.data && openaiResponse.data[0]) {
  generatedImageB64 = openaiResponse.data[0].b64_json;
} else {
  throw new Error('Unexpected OpenAI response format: ' + JSON.stringify(openaiResponse));
}

const prevData = $('Code - Combine Description with Style').item.json;

const binaryData = Buffer.from(generatedImageB64, 'base64');

return {
  json: {
    filename: prevData.filename,
    uuid: prevData.uuid,
    filter: prevData.filter,
    timestamp: prevData.timestamp,
    session_id: prevData.session_id
  },
  binary: {
    data: await this.helpers.prepareBinaryData(binaryData, prevData.filename, 'image/png')
  }
};

