function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0;
    var v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const filterStyles = {
  'cartoon': 'Transform this image into a vibrant cartoon style with bold outlines, bright colors, and smooth cel-shading like a Disney or Pixar animation. Keep the same composition, people, and objects.',
  'oil_painting': 'Transform this image into a classic oil painting with visible brushstrokes, rich textures, and deep colors in the style of Renaissance masters. Keep the same composition, people, and objects.',
  'anime': 'Transform this image into Japanese anime style with large expressive eyes, clean lines, and vibrant colors. Keep the same composition, people, and objects.',
  'vintage': 'Transform this image into a vintage 1970s photograph with warm sepia tones, soft film grain, light leaks, and slight vignetting. Keep the same composition, people, and objects.',
  'neon': 'Transform this image into cyberpunk neon style with glowing neon edges, dark background, and vibrant pink/blue/purple color scheme. Keep the same composition, people, and objects.',
  'watercolor': 'Transform this image into a soft watercolor painting with flowing colors, gentle washes, and delicate brush effects. Keep the same composition, people, and objects.',
  'sketch': 'Transform this image into a detailed pencil sketch with professional shading, hatching, and fine line work. Keep the same composition, people, and objects.',
  'pop_art': 'Transform this image into Andy Warhol pop art style with bold primary colors, halftone dots, and high contrast. Keep the same composition, people, and objects.'
};

const filterName = $json.body.filter.toLowerCase();
const stylePrompt = filterStyles[filterName];

if (!stylePrompt) {
  throw new Error('Invalid filter: ' + filterName);
}

let imageData = $json.body.image;

// Extract raw base64 from data URL if present
let base64Data;
let mimeType = 'image/png';
if (imageData.startsWith('data:')) {
  const matches = imageData.match(/^data:([^;]+);base64,(.+)$/);
  if (matches) {
    mimeType = matches[1];
    base64Data = matches[2];
  } else {
    base64Data = imageData.split(',')[1];
  }
} else {
  base64Data = imageData;
}

// Strip newlines from base64
base64Data = base64Data.replace(/\n/g, '').replace(/\r/g, '');

const uuid = generateUUID();
const filename = uuid + '.png';

// Get model preference (default to 'gemini' for Nano Banana)
const model = ($json.body.model || 'gemini').toLowerCase();

// Convert base64 to binary buffer for multipart upload (GPT Image)
const imageBuffer = Buffer.from(base64Data, 'base64');

return {
  json: {
    filter: filterName,
    stylePrompt: stylePrompt,
    filename: filename,
    uuid: uuid,
    timestamp: new Date().toISOString(),
    session_id: $json.body.session_id || null,
    model: model,
    base64Data: base64Data,
    mimeType: mimeType
  },
  binary: {
    image: await this.helpers.prepareBinaryData(imageBuffer, 'input.png', 'image/png')
  }
};

const response = $json;
const prevData = $('Code - Map Filter to Style').item.json;
let generatedImageB64;

// Check which model was used based on response structure
if (response.data && response.data[0]) {
  // OpenAI GPT Image response format
  generatedImageB64 = response.data[0].b64_json;
} else if (response.candidates && response.candidates[0]) {
  // Gemini/Nano Banana response format
  const parts = response.candidates[0].content.parts;
  for (const part of parts) {
    if (part.inline_data && part.inline_data.data) {
      generatedImageB64 = part.inline_data.data;
      break;
    }
  }
}

if (!generatedImageB64) {
  throw new Error('No image found in AI response: ' + JSON.stringify(response));
}

const binaryData = Buffer.from(generatedImageB64, 'base64');

return {
  json: {
    filename: prevData.filename,
    uuid: prevData.uuid,
    filter: prevData.filter,
    timestamp: prevData.timestamp,
    session_id: prevData.session_id,
    model: prevData.model
  },
  binary: {
    data: await this.helpers.prepareBinaryData(binaryData, prevData.filename, 'image/png')
  }
};

const prevData = $('Code - Map Filter to Style').item.json;

const filename = prevData.filename;
const expiresIn = 1200;

const downloadUrl = 'https://pub-81c17259d0024b8f8f925552b562c490.r2.dev/' + filename;

return {
  json: {
    downloadUrl: downloadUrl,
    filename: filename,
    uuid: prevData.uuid,
    filter: prevData.filter,
    expiresIn: expiresIn,
    expiresAt: new Date(Date.now() + expiresIn * 1000).toISOString(),
    timestamp: prevData.timestamp,
    session_id: prevData.session_id,
    model: prevData.model
  }
};

const signedUrlData = $('Code - Generate Signed URL').item.json;

let qrBase64 = '';
const binaryData = $input.item.binary;
if (binaryData && binaryData.data) {
  const buffer = await this.helpers.getBinaryDataBuffer(0, 'data');
  qrBase64 = buffer.toString('base64');
}

return {
  json: {
    success: true,
    qr_code: qrBase64 ? 'data:image/png;base64,' + qrBase64 : null,
    download_url: signedUrlData.downloadUrl,
    filter_applied: signedUrlData.filter,
    expires_in: signedUrlData.expiresIn,
    expires_at: signedUrlData.expiresAt,
    session_id: signedUrlData.session_id,
    model_used: signedUrlData.model
  }
};
