function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0;
    var v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const filterStyles = {
  'cartoon': 'Transform this exact image into a vibrant cartoon style with bold outlines, bright colors, and smooth cel-shading like a Disney or Pixar animation. Preserve the exact same people, poses, composition, and background.',
  'oil_painting': 'Transform this exact image into a classic oil painting with visible brushstrokes, rich textures, and deep colors in the style of Renaissance masters. Preserve the exact same people, poses, composition, and background.',
  'anime': 'Transform this exact image into Japanese anime style with large expressive eyes, clean lines, and vibrant colors. Preserve the exact same people, poses, composition, and background.',
  'vintage': 'Transform this exact image into a vintage 1970s photograph with warm sepia tones, soft film grain, light leaks, and slight vignetting. Preserve the exact same people, poses, composition, and background.',
  'neon': 'Transform this exact image into cyberpunk neon style with glowing neon edges, dark background, and vibrant pink/blue/purple color scheme. Preserve the exact same people, poses, composition, and background.',
  'watercolor': 'Transform this exact image into a soft watercolor painting with flowing colors, gentle washes, and delicate brush effects. Preserve the exact same people, poses, composition, and background.',
  'sketch': 'Transform this exact image into a detailed pencil sketch with professional shading, hatching, and fine line work. Preserve the exact same people, poses, composition, and background.',
  'pop_art': 'Transform this exact image into Andy Warhol pop art style with bold primary colors, halftone dots, and high contrast. Preserve the exact same people, poses, composition, and background.'
};

const filterName = $json.body.filter.toLowerCase();
const stylePrompt = filterStyles[filterName];

if (!stylePrompt) {
  throw new Error('Invalid filter: ' + filterName);
}

let imageData = $json.body.image;
// Ensure data URL format
if (!imageData.startsWith('data:')) {
  imageData = 'data:image/jpeg;base64,' + imageData;
}

const uuid = generateUUID();
const filename = uuid + '.png';

return {
  json: {
    filter: filterName,
    stylePrompt: stylePrompt,
    imageDataUrl: imageData,
    filename: filename,
    uuid: uuid,
    timestamp: new Date().toISOString(),
    session_id: $json.body.session_id || null
  }
};

const openaiResponse = $json;
let generatedImageB64;

if (openaiResponse.data && openaiResponse.data[0]) {
  generatedImageB64 = openaiResponse.data[0].b64_json;
} else {
  throw new Error('Unexpected OpenAI response format: ' + JSON.stringify(openaiResponse));
}

const prevData = $('Code - Map Filter to Style').item.json;

const binaryData = Buffer.from(generatedImageB64, 'base64');

return {
  json: {
    filename: prevData.filename,
    uuid: prevData.uuid,
    filter: prevData.filter,
    timestamp: prevData.timestamp,
    session_id: prevData.session_id
  },
  binary: {
    data: await this.helpers.prepareBinaryData(binaryData, prevData.filename, 'image/png')
  }
};

const prevData = $('Code - Map Filter to Style').item.json;

const filename = prevData.filename;
const expiresIn = 1200;

const downloadUrl = 'https://pub-81c17259d0024b8f8f925552b562c490.r2.dev/' + filename;

return {
  json: {
    downloadUrl: downloadUrl,
    filename: filename,
    uuid: prevData.uuid,
    filter: prevData.filter,
    expiresIn: expiresIn,
    expiresAt: new Date(Date.now() + expiresIn * 1000).toISOString(),
    timestamp: prevData.timestamp,
    session_id: prevData.session_id
  }
};

const signedUrlData = $('Code - Generate Signed URL').item.json;

let qrBase64 = '';
const binaryData = $input.item.binary;
if (binaryData && binaryData.data) {
  const buffer = await this.helpers.getBinaryDataBuffer(0, 'data');
  qrBase64 = buffer.toString('base64');
}

return {
  json: {
    success: true,
    qr_code: qrBase64 ? 'data:image/png;base64,' + qrBase64 : null,
    download_url: signedUrlData.downloadUrl,
    filter_applied: signedUrlData.filter,
    expires_in: signedUrlData.expiresIn,
    expires_at: signedUrlData.expiresAt,
    session_id: signedUrlData.session_id
  }
};
